import React from 'react';
import logo from './levvel-logo.svg';
import './App.css';

const author1 = JSON.stringify(
  {
    "userId":1,
    "id": 1,
    "title": "Jessica Voss",
    "body": 'Las Vegas Trip'
  }
)


const getData = async () => {
  try{
    const response = await fetch('https://jsonplaceholder.typicode.com/posts', {method: 'PUT', body:author1,
  headers:{'Content-type': 'application/json; charset=UTF-8'}});
    if(response.ok){
      const jsonResponse = await response.json();
      return jsonResponse;
    }
    throw new Error('Request failed!');
  }
  catch(error){
    console.log(error);
  }
}
getData();

class App extends React.Component {
  render() {
    return (
      <div className="App">
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <p>
            lol
          </p>
          <a
            className="App-link"
            href="https://reactjs.org"
            target="_blank"
            rel="noopener noreferrer">

            Learn React
          </a>
        </header>
      </div>
    );
  }
}

class Display extends React.Component { 
   constructor(props) {
       super(props);

       this.state = {
           items: [],
           DataisLoaded: false
       };
   }

   // ComponentDidMount is used to
   // execute the code 
   componentDidMount() {
       fetch("https://jsonplaceholder.typicode.com/posts")
           .then((res) => res.json())
           .then((json) => {
               this.setState({
                   items: json,
                   DataisLoaded: true
               });
           })
   }
   render() {
       const { DataisLoaded, items } = this.state;
       if (!DataisLoaded) return <div>
           <h1> Pleses wait some time.... </h1></div> ;

       return (
       <div className = "App">
           <h1>Top posts by author</h1>  {
               items.map((item) => ( 
               <ol key = { item.id } >
                  { item.userId }
                  <br/>
                  { item.title }
               </ol>
               ))
           }
       </div>
   );
}
}

export default Display;
